var paramObjectCoordinates = [297, 5, 400, 630];
var returnObjectCoordinates = [6, 637, 1200, 437];
var objPadding = 5;
outlets = 2;

var cmdIterIdx = 0;
var cmdList = [];

var currentSelectedDescriptor = "";

function initialise()
{
    currentObj = this.patcher.firstobject;
    removeList = [];
    for(i = 0; i < this.patcher.count; i++)
    {
        if(currentObj.varname.indexOf("jscreated") !== -1)
            removeList.push(currentObj);
        
            currentObj = currentObj.nextobject;
    }

    for(i = 0; i < removeList.length; i++)
    {
        this.patcher.remove(removeList[i]);
    }

    set_descriptor_state("ah_energy", false);
}

function process(print, prtCmd)
{
    printResults = true;
    if(print == 0)
        printResults = false;
    printCommands = false;
    if(prtCmd == 1)
        printCommands = true;

    cmdString = get_instructions();

    cmdIterIdx = 0;

    cmdList = [];
    cmdList = cmdString.split(",");

    if(printCommands)
        post(cmdList[cmdIterIdx] + "\n")

    outlet(0, cmdList[cmdIterIdx]);
}

function process_finished()
{
    cmdIterIdx = cmdIterIdx + 1;
    if(printCommands)
        post(cmdList[cmdIterIdx] + "\n")
    outlet(0, cmdList[cmdIterIdx]);
}

function ah_desc_in(x)
{
    if(cmdIterIdx - 2 == 0)
        theDesc = cmdList[cmdIterIdx - 2].split(" ")[2];
    else
        theDesc = cmdList[cmdIterIdx - 2].split(" ")[3];

    if(printResults)
        post("ah's " + theDesc + ": " + x + "\n")

}

function fl_desc_in(x)
{
    if(printResults)
        post("fl: " + x + "\n")

}

function get_command_string()
{
    cmdString = get_instructions();

    outlet(1, "set " + cmdString);
}

function get_instructions()
{
    mainInstructionString = "";

    for(descriptor in descriptorDict)
    {
        if(descriptorDict[descriptor]["state"])
        {
            for(retType in descriptorDict[descriptor]["calculation"])
            {
                if(descriptorDict[descriptor]["calculation"][retType]["state"])
                {
                    if(descriptor.indexOf("fl_") == 0)
                    {
                        if(descriptor.slice(3) == "centroid" || descriptor.slice(3) == "spread" || descriptor.slice(3) == "skewness" || descriptor.slice(3) == "kurtosis" || descriptor.slice(3) == "rolloff" || descriptor.slice(3) == "flatness" || descriptor.slice(3) == "crest")
                            mainFlObj = "SPSH ";
                        else if(descriptor.slice(3) == "loudness" || descriptor.slice(3) == "true_peak")
                            mainFlObj = "LOUD ";
                        else if(descriptor.slice(3) == "frequency" || descriptor.slice(3) == "confidence")
                            mainFlObj = "PITC ";
                        else if(descriptor.slice(3) == "melbands")
                            mainFlObj = "MELB ";
                        else if(descriptor.slice(3) == "mfcc")
                            mainFlObj = "MFCC ";
                    }
                    
                    if(descriptor.indexOf("ah_") == 0)
                    {
                        instructionString = "AH_DESC descriptors " + String(descriptor).slice(3) + " ";
                        fftString = "AH_DESC fftparams ";
                    }
                    if(descriptor.indexOf("fl_") == 0)
                    {
                        instructionString = "FL_" + mainFlObj;
                        fftString = "FL_" + mainFlObj + "fftsettings ";
                    }
                    
                    for(param in descriptorDict[descriptor]["parameters"])
                    {
                        if(descriptorDict[descriptor]["parameters"][param]["name"].indexOf("FFT") !== -1)
                        {
                            fftString = fftString + String(descriptorDict[descriptor]["parameters"][param]["values"][descriptorDict[descriptor]["parameters"][param]["current"]]) + " ";
                        }
                        else
                        {
                            if(descriptor.indexOf("ah_") == 0)
                            {
                                instructionString = instructionString + String(descriptorDict[descriptor]["parameters"][param]["current"]) + " ";
                            }
                            if(descriptor.indexOf("fl_") == 0)
                            {
                                if(descriptorDict[descriptor]["parameters"][param]["objectMessage"] == "windowsize" || descriptorDict[descriptor]["parameters"][param]["objectMessage"] == "hopsize")
                                    instructionString = instructionString + String(descriptorDict[descriptor]["parameters"][param]["objectMessage"]) + " " + String(descriptorDict[descriptor]["parameters"][param]["values"][descriptorDict[descriptor]["parameters"][param]["current"]]) + " ";
                                else
                                    instructionString = instructionString + String(descriptorDict[descriptor]["parameters"][param]["objectMessage"]) + " " + String(descriptorDict[descriptor]["parameters"][param]["current"]) + " ";
                            } 
                        }
                    }

                    if(descriptor.indexOf("ah_") == 0)
                    {
                        instructionString = instructionString + retType + " ";
                        if("numberReturnValues" in descriptorDict[descriptor]["calculation"][retType])
                            instructionString = instructionString + String(descriptorDict[descriptor]["calculation"][retType]["numberReturnValues"]) + " ";
                        if("maskTime" in descriptorDict[descriptor]["calculation"][retType])
                            instructionString = instructionString + "masktime " + String(descriptorDict[descriptor]["calculation"][retType]["maskTime"]) + " ";
                        if("threshold" in descriptorDict[descriptor]["calculation"][retType])
                            instructionString = instructionString + "threshold " + String(descriptorDict[descriptor]["calculation"][retType]["threshold"]) + " " + descriptorDict[descriptor]["calculation"][retType]["thresholdTypeList"][descriptorDict[descriptor]["calculation"][retType]["thresholdType"]] + " ";
                    }
                    if(descriptor.indexOf("fl_") == 0)
                    {
                        statsString = "FL_STAT " + descriptor + " " + retType + " numderivs " + String(descriptorDict[descriptor]["calculation"][retType]["numderivs"]) + " ";
                        if("low" in descriptorDict[descriptor]["calculation"][retType])
                            statsString = statsString + "low " + String(descriptorDict[descriptor]["calculation"][retType]["low"]) + " ";
                        if("middle" in descriptorDict[descriptor]["calculation"][retType])
                            statsString = statsString + "middle " + String(descriptorDict[descriptor]["calculation"][retType]["middle"]) + " ";
                        if("high" in descriptorDict[descriptor]["calculation"][retType])
                            statsString = statsString + "high " + String(descriptorDict[descriptor]["calculation"][retType]["high"]) + " ";
                    }    
                        
                    mainInstructionString = mainInstructionString + instructionString + ", " + fftString + ", ";
                    if(descriptor.indexOf("ah_") == 0)
                        mainInstructionString = mainInstructionString + "AH_DESC analyse " + analyseBufName + ", "
                    if(descriptor.indexOf("fl_") == 0)
                    {
                        mainInstructionString = mainInstructionString + "FL_" + mainFlObj + " bang, ";
                        mainInstructionString = mainInstructionString + statsString + ", ";
                        mainInstructionString = mainInstructionString + "FL_STAT bang, ";
                    }
                        
                }
            }  
        }
    }

    return mainInstructionString;
}

function param_set(parameter, value)
{
    descriptorDict[currentSelectedDescriptor]["parameters"][parameter]["current"] = value;
}

function return_set(type, param, value)
{
    descriptorDict[currentSelectedDescriptor]["calculation"][type][param] = value;
}

function set_descriptor_state(descriptor, state)
{
    descriptorDict[descriptor]["state"] = false;
    if(state == 1)
        descriptorDict[descriptor]["state"] = true;

    if(currentSelectedDescriptor != descriptor)
    {
        create_parameter_objects(descriptor, paramObjectCoordinates[0], paramObjectCoordinates[1], paramObjectCoordinates[2], paramObjectCoordinates[3]);
        create_return_objects(descriptor, returnObjectCoordinates[0], returnObjectCoordinates[1], returnObjectCoordinates[2], returnObjectCoordinates[3]);
    }
        
    currentSelectedDescriptor = descriptor
}

function create_return_objects(descriptor, x, y, w, h)
{
    //Create Panel:
    backgroundPanel2 = this.patcher.newdefault(x, y, "panel");
    backgroundPanel2.varname = "jscreated";
    backgroundPanel2.rect = [x, y, w + x, h + y];
    backgroundPanel2.message("bgfillcolor", 0, 0, 0, 0);
    backgroundPanel2.message("border", 1);
    backgroundPanel2.message("bordercolor", 1, 0, 0, 1);
    returnObjectList.push(backgroundPanel2);

    eachWidth = w / returnsPerColumn;
    i = 0;
    columnOffset = 0;
    biggestYOffset = 0;
    for(returnType in descriptorDict[descriptor]["calculation"])
    {
        // The label:
        retLabel = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding, "comment");
        retLabel.varname = "jscreated";
        retLabel.message("set", descriptorDict[descriptor]["calculation"][returnType]["name"]); 
        retLabel.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding, eachWidth - objPadding, h);
        returnObjectList.push(retLabel);
        yOffset = retLabel.rect[3] - retLabel.rect[1];

        // The state toggle:
        retToggle = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "toggle");
        retToggle.varname = "jscreated";
        retToggle.message("set", descriptorDict[descriptor]["calculation"][returnType]["state"]);  
        returnObjectList.push(retToggle);
        yOffset = yOffset + retToggle.rect[3] - retToggle.rect[1];

        stateCom = this.patcher.newdefault(x, y, "prepend", "return_set", returnType, "state");
        stateCom.varname = "jscreated";
        stateCom.hidden = true;
        returnObjectList.push(stateCom);
        this.patcher.hiddenconnect(retToggle, 0, stateCom, 0);
        this.patcher.hiddenconnect(stateCom, 0, this.box, 0);

        if("threshold" in descriptorDict[descriptor]["calculation"][returnType])
        {
            threshLabel = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "comment");
            threshLabel.varname = "jscreated";
            threshLabel.message("set", "Threshold:"); 
            threshLabel.message("fontsize", 10); 
            threshLabel.message("fontface", "italic"); 
            threshLabel.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding);
            returnObjectList.push(threshLabel);
            yOffset = yOffset + threshLabel.rect[3] - threshLabel.rect[1];

            //Threashold set:
            threshNum = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "flonum");
            threshNum.varname = "jscreated";
            threshNum.message("set", descriptorDict[descriptor]["calculation"][returnType]["threshold"]);
            threshNum.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding); 
            returnObjectList.push(threshNum);
            yOffset = yOffset + threshNum.rect[3] - threshNum.rect[1];

            thresholdCom = this.patcher.newdefault(x, y, "prepend", "return_set", returnType, "threshold");
            thresholdCom.varname = "jscreated";
            thresholdCom.hidden = true;
            returnObjectList.push(thresholdCom);
            this.patcher.hiddenconnect(threshNum, 0, thresholdCom, 0);
            this.patcher.hiddenconnect(thresholdCom, 0, this.box, 0);
        
            threshTypeLabel = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + yOffset, "comment");
            threshTypeLabel.varname = "jscreated";
            threshTypeLabel.message("set", "Type:"); 
            threshTypeLabel.message("fontsize", 10); 
            threshTypeLabel.message("fontface", "italic"); 
            threshTypeLabel.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding);
            returnObjectList.push(threshTypeLabel);
            yOffset = yOffset + threshTypeLabel.rect[3] - threshTypeLabel.rect[1];

            threshTypeDrop = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + yOffset, "umenu");
            threshTypeDrop.varname = "jscreated";

            for(item = 0; item < descriptorDict[descriptor]["calculation"][returnType]["thresholdTypeList"].length; item++)
            {
                threshTypeDrop.message("append", descriptorDict[descriptor]["calculation"][returnType]["thresholdTypeList"][item]); 
            }
            
            threshTypeDrop.message("set", descriptorDict[descriptor]["calculation"][returnType]["thresholdType"]); 

            threshTypeDrop.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding);
            returnObjectList.push(threshTypeDrop);
            yOffset = yOffset + threshTypeDrop.rect[3] - threshTypeDrop.rect[1];

            threTypeCom = this.patcher.newdefault(x, y, "prepend", "return_set", returnType, "thresholdType");
            threTypeCom.varname = "jscreated";
            threTypeCom.hidden = true;
            returnObjectList.push(threTypeCom);
            this.patcher.hiddenconnect(threshTypeDrop, 0, threTypeCom, 0);
            this.patcher.hiddenconnect(threTypeCom, 0, this.box, 0);
        }

        if("numberReturnValues" in descriptorDict[descriptor]["calculation"][returnType])
        {
            retValLabel = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "comment");
            retValLabel.varname = "jscreated";
            retValLabel.message("set", "No. Return Values:"); 
            retValLabel.message("fontsize", 10); 
            retValLabel.message("fontface", "italic"); 
            retValLabel.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding);
            returnObjectList.push(retValLabel);
            yOffset = yOffset + retValLabel.rect[3] - retValLabel.rect[1];

            retValInt = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "number");
            retValInt.varname = "jscreated";
            retValInt.message("set", descriptorDict[descriptor]["calculation"][returnType]["numberReturnValues"]);
            retValInt.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding); 
            returnObjectList.push(retValInt);
            yOffset = yOffset + retValInt.rect[3] - retValInt.rect[1];

            retValCom = this.patcher.newdefault(x, y, "prepend", "return_set", returnType, "numberReturnValues");
            retValCom.varname = "jscreated";
            retValCom.hidden = true;
            returnObjectList.push(retValCom);
            this.patcher.hiddenconnect(retValInt, 0, retValCom, 0);
            this.patcher.hiddenconnect(retValCom, 0, this.box, 0);
        }

        if("maskTime" in descriptorDict[descriptor]["calculation"][returnType])
        {
            maskLabel = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "comment");
            maskLabel.varname = "jscreated";
            maskLabel.message("set", "Mask Time (ms):"); 
            maskLabel.message("fontsize", 10); 
            maskLabel.message("fontface", "italic"); 
            maskLabel.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding);
            returnObjectList.push(maskLabel);
            yOffset = yOffset + maskLabel.rect[3] - maskLabel.rect[1];

            maskInt = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "number");
            maskInt.varname = "jscreated";
            maskInt.message("set", descriptorDict[descriptor]["calculation"][returnType]["maskTime"]);
            maskInt.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding); 
            returnObjectList.push(maskInt);
            yOffset = yOffset + maskInt.rect[3] - maskInt.rect[1];

            maskCom = this.patcher.newdefault(x, y, "prepend", "return_set", returnType, "maskTime");
            maskCom.varname = "jscreated";
            maskCom.hidden = true;
            returnObjectList.push(maskCom);
            this.patcher.hiddenconnect(maskInt, 0, maskCom, 0);
            this.patcher.hiddenconnect(maskCom, 0, this.box, 0);
        }

        if("numderivs" in descriptorDict[descriptor]["calculation"][returnType])
        {
            maskLabel = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "comment");
            maskLabel.varname = "jscreated";
            maskLabel.message("set", "Number of Derivations:"); 
            maskLabel.message("fontsize", 10); 
            maskLabel.message("fontface", "italic"); 
            maskLabel.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding);
            returnObjectList.push(maskLabel);
            yOffset = yOffset + maskLabel.rect[3] - maskLabel.rect[1];

            derivInt = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "number");
            derivInt.varname = "jscreated";
            derivInt.message("set", descriptorDict[descriptor]["calculation"][returnType]["numderivs"]);
            derivInt.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding); 
            returnObjectList.push(derivInt);
            yOffset = yOffset + derivInt.rect[3] - derivInt.rect[1];

            derivCom = this.patcher.newdefault(x, y, "prepend", "return_set", returnType, "numderivs");
            derivCom.varname = "jscreated";
            derivCom.hidden = true;
            returnObjectList.push(derivCom);
            this.patcher.hiddenconnect(derivInt, 0, derivCom, 0);
            this.patcher.hiddenconnect(derivCom, 0, this.box, 0);
        }

        if("low" in descriptorDict[descriptor]["calculation"][returnType] || "middle" in descriptorDict[descriptor]["calculation"][returnType] || "high" in descriptorDict[descriptor]["calculation"][returnType])
        {
            if("low" in descriptorDict[descriptor]["calculation"][returnType])
            {
                theMessage = "low";
                theLabel = "Low percentage:";
            }
            else if("middle" in descriptorDict[descriptor]["calculation"][returnType])
            {
                theMessage = "middle";
                theLabel = "Middle percentage:";
            }
            else if("high" in descriptorDict[descriptor]["calculation"][returnType])
            {
                theMessage = "high";
                theLabel = "High percentage:";
            }

            percLabel = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "comment");
            percLabel.varname = "jscreated";
            percLabel.message("set", theLabel); 
            percLabel.message("fontsize", 10); 
            percLabel.message("fontface", "italic"); 
            percLabel.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding);
            returnObjectList.push(percLabel);
            yOffset = yOffset + percLabel.rect[3] - percLabel.rect[1];

            percFloat = this.patcher.newdefault(x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, "flonum");
            percFloat.varname = "jscreated";
            percFloat.message("set", descriptorDict[descriptor]["calculation"][returnType][theMessage]);
            percFloat.message("patching_rect", x + (eachWidth * i) + objPadding, columnOffset + y + objPadding + yOffset, eachWidth - objPadding); 
            returnObjectList.push(percFloat);
            yOffset = yOffset + percFloat.rect[3] - percFloat.rect[1];

            percCom = this.patcher.newdefault(x, y, "prepend", "return_set", returnType, theMessage);
            percCom.varname = "jscreated";
            percCom.hidden = true;
            returnObjectList.push(percCom);
            this.patcher.hiddenconnect(percFloat, 0, percCom, 0);
            this.patcher.hiddenconnect(percCom, 0, this.box, 0);
        }

        i = i + 1;

        if(yOffset > biggestYOffset)
            biggestYOffset = yOffset;

        if(i % returnsPerColumn == 0)
        {
            i = 0;
            columnOffset = columnOffset + biggestYOffset;
            biggestYOffset = 0;
        }
    }
}

function create_parameter_objects(descriptor, x, y, w, h)
{
    for(object = 0; object < paramObjectList.length; object++)
    {
        this.patcher.remove(paramObjectList[object]);
    }

    for(object = 0; object < returnObjectList.length; object++)
    {
        this.patcher.remove(returnObjectList[object]);
    }

    //Create Panel:
    backgroundPanel = this.patcher.newdefault(x, y, "panel");
    backgroundPanel.varname = "jscreated";
    backgroundPanel.rect = [x, y, w + x, h + y];
    backgroundPanel.message("bgfillcolor", 0, 0, 0, 0);
    backgroundPanel.message("border", 1);
    backgroundPanel.message("bordercolor", 1, 0, 0, 1);
    paramObjectList.push(backgroundPanel);

    // Create title:
    titleComment = this.patcher.newdefault(x + objPadding, y + objPadding, "comment");
    titleComment.varname = "jscreated";
    titleComment.message("fontsize", 14);
    titleComment.message("fontface", "bold");
    titleComment.message("set", descriptorDict[descriptor]["name"]);
    titleComment.message("patching_rect", x + objPadding, y + objPadding, w - (objPadding * 2), h);
    paramObjectList.push(titleComment);

    // Create description:
    descComment = this.patcher.newdefault(x + objPadding, titleComment.rect[3], "comment");
    descComment.varname = "jscreated";
    descComment.message("fontface", "italic");
    descComment.message("set", descriptorDict[descriptor]["desc"]);
    descComment.message("patching_rect", x + objPadding, titleComment.rect[3], w - (objPadding * 2), h);
    paramObjectList.push(descComment);

    parameterList = descriptorDict[descriptor]["parameters"];
    paramNewY = descComment.rect[3];
    paramHeight = (h - paramNewY) / parameterList.length;

    for(param = 0; param < parameterList.length; param++)
    {
        paramLabel = this.patcher.newdefault(x + objPadding, paramNewY + (paramHeight * param), "comment");
        paramLabel.varname = "jscreated";
        paramLabel.message("set", parameterList[param]["name"]);
        paramLabel.message("textjustification", 2);
        paramObjectList.push(paramLabel);

        paramDesc = this.patcher.newdefault(x + objPadding, paramNewY + (paramHeight * param), "comment");
        paramDesc.varname = "jscreated";
        paramDesc.message("set", parameterList[param]["desc"]);
        paramDesc.message("fontface", "italic");
        paramDesc.message("fontsize", 10);
        paramDesc.message("textjustification", 1);
        paramObjectList.push(paramDesc);

        communicationObj = this.patcher.newdefault(x, y, "prepend", "param_set", String(param));
        communicationObj.hidden = true;
        communicationObj.varname = "jscreated";
        paramObjectList.push(communicationObj);

        if(parameterList[param]["type"] == "toggle")
        {
            addedObj = this.patcher.newdefault((x + objPadding) + (w * 0.5), paramNewY + (paramHeight * param), "toggle");
            addedObj.varname = "jscreated";
            addedObj.message("set", parameterList[param]["current"]);
            addedObj.message("patching_rect", (x + objPadding) + (w * 0.5), (paramNewY + (paramHeight * param)) + ((paramHeight * 0.5) - (addedObj.rect[3] - addedObj.rect[1])), addedObj.rect[2] - addedObj.rect[0], addedObj.rect[3] - addedObj.rect[1]);
           
            paramObjectList.push(addedObj);

            this.patcher.hiddenconnect(addedObj, 0, communicationObj, 0);
            this.patcher.hiddenconnect(communicationObj, 0, this.box, 0);  
        }

        else if(parameterList[param]["type"] == "int")
        {
            addedObj = this.patcher.newdefault((x + objPadding) + (w * 0.5), paramNewY + (paramHeight * param), "number");
            addedObj.varname = "jscreated";
            addedObj.message("min", parameterList[param]["minimum"]); 
            addedObj.message("max", parameterList[param]["maximum"]); 
            addedObj.message("set", parameterList[param]["current"]);
            addedObj.message("patching_rect", (x + objPadding) + (w * 0.5), (paramNewY + (paramHeight * param)) + ((paramHeight * 0.5) - (addedObj.rect[3] - addedObj.rect[1])), (w * 0.5) - (objPadding * 2), addedObj.rect[3] - addedObj.rect[1]);

            paramObjectList.push(addedObj);

            this.patcher.hiddenconnect(addedObj, 0, communicationObj, 0);
            this.patcher.hiddenconnect(communicationObj, 0, this.box, 0);  
        }

        else if(parameterList[param]["type"] == "float")
        {
            addedObj = this.patcher.newdefault((x + objPadding) + (w * 0.5), paramNewY + (paramHeight * param), "flonum");
            addedObj.varname = "jscreated";
            addedObj.message("min", parameterList[param]["minimum"]); 
            addedObj.message("max", parameterList[param]["maximum"]); 
            addedObj.message("set", parameterList[param]["current"]);
            addedObj.message("patching_rect", (x + objPadding) + (w * 0.5), (paramNewY + (paramHeight * param)) + ((paramHeight * 0.5) - (addedObj.rect[3] - addedObj.rect[1])), (w * 0.5) - (objPadding * 2), addedObj.rect[3] - addedObj.rect[1]);

            paramObjectList.push(addedObj);

            this.patcher.hiddenconnect(addedObj, 0, communicationObj, 0);
            this.patcher.hiddenconnect(communicationObj, 0, this.box, 0);  
        }

        else if(parameterList[param]["type"] == "drop")
        {
            addedObj = this.patcher.newdefault((x + objPadding) + (w * 0.5), paramNewY + (paramHeight * param), "umenu");
            addedObj.varname = "jscreated";

            for(i = 0; i < parameterList[param]["values"].length; i++)
            {
                addedObj.message("append", parameterList[param]["values"][i]); 
            }
            
            addedObj.message("set", parameterList[param]["current"]); 
            addedObj.message("patching_rect", (x + objPadding) + (w * 0.5), (paramNewY + (paramHeight * param)) + ((paramHeight * 0.5) - (addedObj.rect[3] - addedObj.rect[1])), (w * 0.5) - (objPadding * 2), addedObj.rect[3] - addedObj.rect[1]);

            paramObjectList.push(addedObj);

            this.patcher.hiddenconnect(addedObj, 0, communicationObj, 0);
            this.patcher.hiddenconnect(communicationObj, 0, this.box, 0);  
        }

        paramLabel.message("patching_rect", x + objPadding, (paramNewY + (paramHeight * param)) + ((paramHeight * 0.5) - (paramLabel.rect[3] - paramLabel.rect[1])), (w * 0.5) - objPadding, (paramLabel.rect[3] - paramLabel.rect[1]));
        paramDesc.message("patching_rect", x + objPadding, (paramNewY + (paramHeight * param)) + (paramHeight * 0.5) , w - (objPadding * 2), (paramDesc.rect[3] - paramDesc.rect[1]));
    }

    this.patcher.sendtoback(backgroundPanel);
}

initialise();
